        <thead>
            <tr>
                <th>Etablissement</th>
                <th>Ville</th>
                <th>Distanciel</th>
                <th>Date Début</th>
                <th>Date Fin</th>
                <th>Nom</th>
                <th>Spécialité</th>
            </tr>
        </thead>
        <tbody>
        {% for formation in formationDiplomes %}
            <tr>
               
                <td>{{ formation.nomEtablissement }}</td>
                <td>{{ formation.villeEtablissement }}</td>
                <td>{{ formation.distanciel ? 'Yes' : 'No' }}</td>
                <td>{{ formation.dateD ? formation.dateD|date('d-m-Y') : '' }}</td>
                <td>{{ formation.dateF ? formation.dateF|date('Y-m-d') : 'en cours' }}</td>
                <td>{{ formation.nomFormation }}</td>
                <td>{{ formation.specialite }}</td>
               
            </tr>
        {% else %}
            <tr>
                <td colspan=\"9\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}
